import React, { useState } from 'react';
import cornerstone from 'cornerstone-core';
import dicomParser from 'dicom-parser';

function DicomUploader() {
  const [dicomFile, setDicomFile] = useState(null);

  const handleDicomUpload = (event) => {
    const file = event.target.files[0];
    const fileReader = new FileReader();
    fileReader.onload = (e) => {
      const dicomData = e.target.result;
      const dataSet = dicomParser.parseDicom(dicomData);
      cornerstone.enable(document.getElementById('dicom-canvas'));
      cornerstone.loadAndCacheData(dataSet.byteArray.buffer).then(image => {
        cornerstone.displayImage(document.getElementById('dicom-canvas'), image);
      });
      setDicomFile(file);
    };
    fileReader.readAsArrayBuffer(file);
  };

  return (
    <div>
      <h2>Upload DICOM file</h2>
      <input type="file" accept=".dcm" onChange={handleDicomUpload} />
      <div>
        {dicomFile && (
          <canvas id="dicom-canvas" width="512" height="512"></canvas>
        )}
      </div>
    </div>
  );
}

export default DicomUploader;
