const Item = ({
  id,
  number,
  fileName,
  fileStatus,
  processingProgress,
  fileSize,
  setUploadFile,
}) => {
  function deleteItem() {
    setUploadFile(function (prev) {
      return prev.filter((item) => item.id !== id);
    });
  }
  let statusClassName = '';
  if (fileStatus === 'PROCESSING') {
    statusClassName = 'processing';
  } else if (fileStatus === 'READY') {
    statusClassName = 'ready';
  } else if (fileStatus === 'SUCCESS') {
    statusClassName = 'success';
  } else if (fileStatus === 'FAIL') {
    statusClassName = 'fail';
  }
  function uploadItem() {
    setUploadFile(function (prev) {
      const newUploadFile = prev.map((item) => {
        if (item.id === id) {
          return {
            ...item,
            fileStatus: "SUCCESS",
          };
        }
        return item;
      });
      return newUploadFile;
    });
  }
  return (
    <tr>
      <td>{number}</td>
      <td>{fileName}</td>
      <td className={fileStatus === "READY" ? "ready" : "success"}>{fileStatus}</td>
      <td>Created&ensp;Time:{processingProgress}</td>
      <td>{fileSize}</td>
      <td>
        {/* 代改 */}
        <button onClick={uploadItem} className="btnUpload">Upload</button>
        <button onClick={deleteItem} className="btnReset">Delete</button>
      </td>
    </tr>
  );
};

export default Item;
