const Item = ({
  id,
  number,
  fileName,
  fileStatus,
  processingProgress,
  fileSize,
  setUploadFile,
  setstatus
}) => {
  function deleteItem() {
    setUploadFile(function (prev) {
      return prev.filter((item) => item.id !== id);
    });
  }
  function status() {
    setstatus(function (prev) {
      return prev.map((file) => {
        if (file.fileStatus === "READY") {
          return {
            ...file,
            fileStatus: "SUCCESS",
          };
        }
        return file;
      });
    });
  }
  return (
    <tr className="item">
      <td>{number}</td>
      <td>{fileName}</td>
      <td>{fileStatus}</td>
      <td>Created&ensp;Time:{processingProgress}</td>
      <td>{fileSize}</td>
      <td>
        {/* 代改 */}
        <button >單向上傳</button>
        <button onClick={deleteItem} className="remove">
          Delete
        </button>
      </td>
    </tr>
  );
};

export default Item;
