const Item = ({
  id,
  number,
  fileName,
  fileStatus,
  processingProgress,
  fileSize,
  setUploadFile,
}) => {
  function deleteItem() {
    setUploadFile(function (prev) {
      return prev.filter((item) => item.id !== id);
    });
  }
  function uploadItem() {
    setUploadFile(function (prev) {
      const newUploadFile = prev.map((item) => {
        if (item.id === id) {
          return {
            ...item,
            fileStatus: "UPLOADING",
          };
        }
        return item;
      });
      return newUploadFile;
    });

    // 模擬上傳中的延遲，這裡使用 setTimeout 函式
    setTimeout(() => {
      // 假設上傳成功
      setUploadFile(function (prev) {
        const newUploadFile = prev.map((item) => {
          if (item.id === id) {
            return {
              ...item,
              fileStatus: "SUCCESS",
            };
          }
          return item;
        });
        return newUploadFile;
      });
    }, 3000);
  }
  return (
    <tr>
      <td>{number}</td>
      <td>{fileName}</td>
      <td className={fileStatus === "READY" ? "ready" : "success"}>{fileStatus}</td>
      <td>Created&ensp;Time:{processingProgress}</td>
      <td>{fileSize}</td>
      <td>
        {/* 代改 */}
        <button onClick={uploadItem} className="btnUpload">Upload</button>
        <button onClick={deleteItem} className="btnReset">Delete</button>
      </td>
    </tr>
  );
};

export default Item;
